import React from 'react';
import './App.css';
import {Component} from "react";

class NewComponent extends Component {
    thing(num) {
        return num + 1;
    }
    render() {
        return (
            <div>
                <h1>Hello, world!</h1>
            </div>
        );
    }
}


class Counter extends Component {
    constructor(props) {
        super(props);
        this.state = {count: 0};
    }

    render() {
        return (
            <div>
                <button onClick={() => this.setState({count: this.state.count + 1})}>+</button>
                <p>Count: {this.state.count}</p>
                <button onClick={() => this.setState({count: this.state.count - 1})}>-</button>
            </div>
        );
    }
}

function NavBar() {
    // When the user scrolls the page, execute myFunction
    window.onscroll = function() {myFunction()};

// Get the header
    let header = document.getElementById("myHeader");

// Get the offset position of the navbar
    let sticky = header.offsetTop;

// Add the sticky class to the header when you reach its scroll position. Remove "sticky" when you leave the scroll position
    function myFunction() {
        if (window.pageYOffset > sticky) {
            NavBar.classList.add("sticky");
        } else {
            NavBar.classList.remove("sticky");
        }
    }
    return (
        <div className="NavBar">
            <a href="https://www.buckeyecandycompany.com">Home</a>
            <a href="https://www.buckeyecandycompany.com/about">About</a>
            <a href="https://www.buckeyecandycompany.com/contact">Contact</a>
        </div>
    );
}

function App() {
  return (
    <div className="App">
        <NavBar/>
        <header className="App-header">
            <img src="http://www.buckeyecandycompany.com/img/BuckeyeCandyCompany.svg" className="App-logo" alt="logo"/>
            <p>
                Edit <code>src/App.js</code> and save to reload.
            </p>
            <a
                className="App-link"
                href="https://reactjs.org"
                target="_blank"
                rel="noopener noreferrer"
            >
                Learn React
            </a>
        </header>
        <Counter/>
        <NewComponent/>
    </div>
  );
}

export default App;
